import React from "react";
import { useState, useEffect } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js";
import axios from "axios";

// Usage of mondaySDK example, for more information visit here: https://developer.monday.com/apps/docs/introduction-to-the-sdk/
const monday = mondaySdk();

const App = () => {
  const [context, setContext] = useState();
  const [vinyls, setVinyls] = useState([]);
  const [jobCost, setJobCost] = useState(null);
  const corsProxy = 'https://cors-anywhere.herokuapp.com/';

  useEffect(() => {
      const fetchData = async () => {
          try {
              // First API call to list vinyls
              const vinylUrl = `${corsProxy}https://api.stickermania818.com/listvinyls`;
              const vinylResponse = await fetch(vinylUrl);
              if (!vinylResponse.ok) {
                  throw new Error(`Error: ${vinylResponse.statusText}`);
              }
              const vinylsData = await vinylResponse.json();
              setVinyls(vinylsData);

              // Second API call to get job cost
              const jobCostUrl = `${corsProxy}https://api.stickermania818.com/jobcost?vinylType=MATTE6MIL&width=4&height=5&count=100&includeColor=true&colorPercent=100&colorPasses=1&includeWhite=true&whitePercent=100&whitePasses=1&includeGloss=true&glossPercent=100&glossPasses=1&targetCostPerPrint=1.50&bSalesTax=true&bSalesCommission=true`;
              const jobCostResponse = await fetch(jobCostUrl);
              if (!jobCostResponse.ok) {
                  throw new Error(`Error: ${jobCostResponse.statusText}`);
              }
              const jobCostData = await jobCostResponse.json();
              setJobCost(jobCostData);
          } catch (error) {
              console.error('Error fetching data:', error);
          }
      };

      fetchData();
  }, []);



  useEffect(() => {
    // Notice this method notifies the monday platform that user gains a first value in an app.
    // Read more about it here: https://developer.monday.com/apps/docs/mondayexecute#value-created-for-user/
    monday.execute("valueCreatedForUser");

    // TODO: set up event listeners, Here`s an example, read more here: https://developer.monday.com/apps/docs/mondaylisten/
    monday.listen("context", (res) => {
      setContext(res.data);
    });
  }, []);

  //Some example what you can do with context, read more here: https://developer.monday.com/apps/docs/mondayget#requesting-context-and-settings-data
  const attentionBoxText = `Hello, your user_id is: ${
    context ? context.user.id : "still loading"
  }.
  Let's start building your amazing app, which will change the world!`;

  return (
    <div className="App">
      <AttentionBox
        title="Hello Monday Apps!"
        text={'Vinyl: ' + vinyls[0] + ' Job Cost: ' + jobCost}
        type="success"
      />
    </div>
  );
};

export default App;
